import React from 'react';
import { Input, message } from 'antd';
import './PaperScissorsRock.css';
//import lang from '../files/lang.json';
import RockImg from '../img/Rock.png';
import PaperImg from '../img/Paper.png';
import ScissorsImg from '../img/Scissors.png';
const { Search } = Input;


class PaperScissorsRock extends React.Component {
  constructor() {
    super();
    this.state={
      playerName:'',
      featuresGame:{
        firstFeat: RockImg,
        secondFeat: PaperImg,
        third: ScissorsImg
      }
    }
  }

  setPlayerName = (name) => {
    if (name!==''){
      this.setState({playerName:name})
    } else {
      message.error('Nie wybrano imienia gracza!');
    }


  }

  playGame = ( selectedElement ) => {

  }


  render() {
    return (
      <div className="container paper-scissor-rock-container">
        <h3>Podaj swoje imię i zagraj !</h3>
        <Search
          className="input-set-name"
          placeholder="Podaj swoje imię"
          enterButton="OK"
          size="large"
          onSearch={value => this.setPlayerName.bind( value)}
        />
        <div className="player-space">
          <div className="single-field">
             <img src={this.state.featuresGame.firstFeat} alt="img" width={100} height={100} />
          </div>
          <div className="single-field">
            <img src={this.state.featuresGame.secondFeat} alt="img" width={100} height={100} />
          </div>
          <div className="single-field">
            <img src={this.state.featuresGame.third} width={100} alt="img" height={100} />
          </div>
        </div>

        <div className="computer-space">
        <div className="single-field">
             <img src={this.state.featuresGame.firstFeat} alt="img" width={100} height={100} />
          </div>
          <div className="single-field">
            <span>VS</span>
          </div>
          <div className="single-field">
            <img src={this.state.featuresGame.third} width={100} alt="img" height={100} />
          </div>
        </div>
        <h3>Wygrał: komputer</h3>



      </div>
    );
  }
}


export default PaperScissorsRock;
